package main

import (
	"log"

	"github.com/Clan-Labs/RoGo/account"

	"github.com/Clan-Labs/RoGo/group"
)

func main() {

	acc := account.New("_|WARNING:-DO-NOT-SHARE-THIS.--Sharing-this-will-allow-someone-to-log-in-as-you-and-to-steal-your-ROBUX-and-items.|_")

	//Check initial shout
	shout, err := group.GetShout(4953490, acc)
	if err != nil {
		log.Fatal(err)
	}
	println(shout.Content)

	//Change shout
	err = group.PostShout("[ENTRY] Join our communication server to get accepted into the group - /yxHDRfS", 4953490, acc)
	if err != nil {
		log.Fatal("SHOUTERR "+err.Error())
	}

	//Check shout
	shout, err = group.GetShout(4953490, acc)
	if err != nil {
		log.Fatal(err)
	}
	println(shout.Content)

}
